// axios.defaults.withCredentials = true;
class App extends React.Component {


    constructor(props) {
        super(props);
        this.state =
        {
            users: [],
            email: '',
            expiration_date: '',
            id: 0,
            message: {
                'text': '',
                'alert': ''
            }
        }
    }
    componentDidMount() {
        axios.get("http://192.168.0.21:5000/users")
            .then((res) =>
                this.setState({
                    users: res.data.users,
                    email: '',
                    expiration_date: '',
                    id: 0
                }, () => {
                    console.log(this.state.users);
                })
            )
    }

    emailchange = event => {
        this.setState({
            email: event.target.value
        })
    }

    datechange = event => {
        this.setState({
            expiration_date: event.target.value
        })
    }

    submit(event, id) {
        const headers = {
            "Content-Type": "application/json",
            "Access-Control-Allow-Origin": "*",
            "Access-Control-Allow-Headers": "*"
        }
        event.preventDefault()
        if (id === 0) {
            console.log('entrou no if', id)
                axios.post('http://192.168.0.21:5000/users',
                {
                    "email": this.state.email,
                    "expiration_date": this.state.expiration_date
                })
                .then((response) => {
                    if (response.status === 200){
                        this.setState({
                            message: {
                                'text': 'Usuário cadastrado com sucesso!',
                                'alert': 'success'
                            }})
                    }
                    this.componentDidMount();
                })
                .catch(function (error) {
                    console.log('err:', error)
                });
        } else {
            console.log('entrou no else', id)
            axios.put(`http://192.168.0.21:5000/users/${id}`,
                {
                    "email": this.state.email,
                    "expiration_date": this.state.expiration_date
                })
                .then(() => {
                    this.setState({
                        message: {
                            'text': 'Usuário atualizado com sucesso!',
                            'alert': 'success'
                        }})
                    this.componentDidMount();
                })
                .catch(function (error) {
                    console.log('err:', error);
                });
        }
    }

    delete(id) {
        axios.delete(`http://192.168.0.21:5000/users/${id}`)
            .then(() => {
                this.setState({
                    message: {
                        'text': 'Usuário deletado com sucesso!',
                        'alert': 'danger'
                    }})
                this.componentDidMount()
            })
    }

    getone(id) {
        axios.get(`http://192.168.0.21:5000/users/${id}`)
            .then((res) => {
                this.setState({
                    email: res.data.email,
                    expiration_date: res.data.expiration_date,
                    id: res.data.id
                })
            })
    }

    submitLogin = (async () => {
        const [email, setEmail] = useState("");
        const [password, setPassword] = useState("");
        var [token,] = useState("");

        const requestOptions = {
          method: "POST",
          headers: { "Content-Type": "application/x-www-form-urlencoded" },
          body: { 
            login: email,
            password: password
          }
        }
        const response = await axios.post("http://192.168.0.21:5000/login", 
          { 
            login: email,
            password: password
          }).catch(function (error) {
            console.log('err:', error)
            });
          if (response.status === 200) {
            token = response.data.access_token;
          } else {
            console.log('error')
          }
          console.log('value', token)

          return token

    });
    handleSubmit = (e) => {
        e.preventDefault();
        submitLogin();
    };
    render() {
        console.log('conteudo', token)
        return (

            <div className="container mt-5">
                 {!token ? ( <div className="container">
            <form className="box text-center" onSubmit={handleSubmit}>
              <h3 className="title text-center">Login</h3>
              <div className="field text-center mt-2">
                <label className="label">Usuário</label>
                <div className="control">
                  <input
                    type="text"
                    placeholder="Usuário"
                    value={email}
                    onChange={(e) => thissetEmail(e.target.value)}
                    className="input"
                    required
                  />
                </div>
              </div>
              <div className="field text-center mt-2">
                <label className="label">Senha</label>
                <div className="control">
                  <input
                    type="password"
                    placeholder="Senha"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    className="input"
                    required
                  />
                </div>
              </div>
              {/* <ErrorMessage message={errorMessage} /> */}
              <br />
              <button className="button is-primary" type="submit">
                Login
              </button>
            </form>
          </div>) : ( 
                <div className="row mt-5">

                    <div className="mt-2">
                        {
                            this.state.message.text !== '' ? (
                                <Alert color={this.state.message.alert} className="text-center mt-2"> {this.state.message.text}</Alert>
                            ) : ''
                        }
                    </div>
                    <div className="col lg-6 mt-2">
                        <form onSubmit={(e) => { this.submit(e, this.state.id) }}>
                            <div className="form-group">
                                <input type="email" onChange={(e) => { this.emailchange(e) }} className="form-control mt-2" placeholder="E-mail" value={this.state.email} />
                            </div>
                            <div className="form-group">
                                <input type="date" onChange={(e) => { this.datechange(e) }} className="form-control mt-2" placeholder="Data de expiração" value={this.state.expiration_date} />
                            </div>
                            <button className="btn btn-block btn-primary mt-2">Enviar</button>
                        </form>
                    </div>
                    <div className="col lg-6 mt-2">
                        <table className="table">
                            <thead>
                                <tr>
                                    <th>E-mail</th>
                                    <th>Data de expiração</th>
                                    <th>Editar</th>
                                    <th>Deletar</th>
                                </tr>
                            </thead>
                            <tbody>

                                {this.state.users.map(user =>
                                    <tr>
                                        <td>{user.email}</td>
                                        <td>{user.expiration_date}</td>
                                        <td>
                                            <button onClick={(e) => this.getone(user.id)} className="btn btn-sm btn-primary">
                                                <i className="fa fa-pencil"></i>
                                            </button>
                                        </td>
                                        <td>
                                            <button onClick={(e) => this.delete(user.id)} className="btn btn-sm btn-danger">
                                                <i className="fa fa-trash"></i>
                                            </button>
                                        </td>
                                    </tr>

                                )}
                            </tbody>
                        </table>
                    </div>


                </div>
                )}
            </div>
        );
    }
}

export default App;
