class App extends React.Component {


    constructor(props) {
        super(props);
        this.state =
        {
            users: [],
            email: '',
            expiration_date: '',
            id: 0,
            email_login: '',
            email_password: '',
            token: '',
            message: {
                'text': '',
                'alert': ''
            }
        }
    }
    componentTeste(){
        return 0
    }
    componentDidMount() {
        axios.get("http://192.168.0.21:5000/users")
            .then((res) =>
                this.setState({
                    users: res.data.users,
                    email: '',
                    expiration_date: '',
                    id: 0
                })
            )
    }

    emailchange = event => {
        this.setState({
            email: event.target.value
        })
    }

    datechange = event => {
        this.setState({
            expiration_date: event.target.value
        })
    }

    submit(event, id) {
        const headers = {
            "Content-Type": "application/json",
            "Access-Control-Allow-Origin": "*",
            "Access-Control-Allow-Headers": "*"
        }
        event.preventDefault()
        if (id === 0) {
            console.log('entrou no if', id)
                axios.post('http://192.168.0.21:5000/users',
                {
                    "email": this.state.email,
                    "expiration_date": this.state.expiration_date
                })
                .then((response) => {
                    if (response.status === 200){
                        this.setState({
                            message: {
                                'text': 'Usuário cadastrado com sucesso!',
                                'alert': 'success'
                            }})
                    }
                    this.componentDidMount();
                })
                .catch(function (error) {
                    console.log('err:', error)
                });
        } else {
            axios.put(`http://192.168.0.21:5000/users/${id}`,
                {
                    "email": this.state.email,
                    "expiration_date": this.state.expiration_date
                })
                .then(() => {
                    this.setState({
                        message: {
                            'text': 'Usuário atualizado com sucesso!',
                            'alert': 'success'
                        }})
                    this.componentDidMount();
                })
                .catch(function (error) {
                    console.log('err:', error);
                });
        }
    }

    delete(id) {
        axios.delete(`http://192.168.0.21:5000/users/${id}`)
            .then(() => {
                this.setState({
                    message: {
                        'text': 'Usuário deletado com sucesso!',
                        'alert': 'danger'
                    }})
                this.componentDidMount()
            })
    }

    getone(id) {
        axios.get(`http://192.168.0.21:5000/users/${id}`)
            .then((res) => {
                this.setState({
                    email: res.data.email,
                    expiration_date: res.data.expiration_date,
                    id: res.data.id
                })
            })
    }

